    }
}

.rotating-chakra {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 700px;
    height: 700px;
    transform: translate(-50%, -50%);
    animation: rotate 60s linear infinite;
    opacity: 0.25;
    pointer-events: none;
    z-index: 1;
    filter: brightness(1.5);
}

.service-card {
    transition: all 0.3s ease;
}

.service-card:nth-child(1) {
    animation-delay: 0s;
}

.service-card:nth-child(2) {
    animation-delay: 2s;
}

